import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { login } from '../store/auth';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: ''
        }
    }

    onSubmit = (e) => {
        e.preventDefault();

        const newClient = this.state;
        this.props.login(newClient,this.props.history);
    };

    onChange = (e) => this.setState({ [e.target.name]: e.target.value });

    render() {
        const error = this.props.error;

        return (
        <div className="wrapper">
            <div className="row">
            <div class="form__container">
                <div class="login-screen">
                    <div class="app-title">
                        <h1 className="heading-primary--main">Journal</h1>
                    </div>

                    <form onSubmit={this.onSubmit} class="login-form">
                        <div class="control-group">
                        <input type="text" class="login-field"  name="username" value={this.state.username} onChange={this.onChange} placeholder="username" id="login-name"/>
                        <label class="login-field-icon fui-user" for="login-name"></label>
                        </div>

                        <div class="control-group">
                        <input type="password" class="login-field" onChange={this.onChange} value={this.state.password} placeholder="password" id="login-pass"/>
                        <label class="login-field-icon fui-lock" for="login-pass"></label>
                        </div>

                        <input type="submit" value="Login" class="btn btn-primary btn-large btn-block"/>
                        {error && error.response && <div className="error"> {error.response.data} </div>}

                        <Link to={'/register'} class="login-link">New User? Register Now!</Link>
                    </form>
                </div>
	        </div>
                {/* <div className="form__container">
                    <div className="form__container-header">
                        <h2 className="heading-primary--main">Jornal</h2>
                    </div>



                    <div className="form__container-body">
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group">
                                <label htmlFor="username">Username</label>
                                <input 
                                    type="text" 
                                    className="form-control"
                                    name="username"
                                    placeholder="Username"
                                    required
                                    onChange={this.onChange}
                                    value={this.state.username}
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="username">Password</label>
                                <input 
                                    type="text" 
                                    className="form-control"
                                    name="username"
                                    placeholder="Username"
                                    required
                                    onChange={this.onChange}
                                    value={this.state.username}
                                />
                            </div>

                            <div className="form__container-header">
                                <h2 className="heading-tertiary">
                                    <Link to={'/register'}>New User? Register Now!</Link>
                                </h2>
                            </div>

                            <div className="form__group-btn">
                                <input type="submit" value="Login" className="btn-add" id="main-btn"/>
                            </div>
                            {error && error.response && <div className="error"> {error.response.data} </div>}
                        </form>
                    </div>
                </div> */}
            </div>
         </div>
        )
    }
}

const mapState = state => {
    return {
        username: state.user.username,
        userId: state.user.id,
        error: state.auth.error
    }
}

export default connect(mapState, { login })(Login);
